package apiday02;/** * String 字符串有一个比较常用的支持正则表达式的方法： * boolean matches（String regex） * 使用给定的正则表达式验证当前字符串是否符合要求 */public class MatchesDemo {    public static void main(String[] args) {        /*        邮箱的正则表达式：          [a-zA-Z0-9_ ]+@[a-zA-Z0-9]+(\.[a-zA-Z]+)+         */        String email = "saber@qq.cn";        String regex = "[a-zA-Z0-9_ ]+@[a-zA-Z0-9]+(\\.[a-zA-Z]+)+";        boolean match = email.matches(regex);        if (match){            System.out.println("是正确的邮箱");        }else{            System.out.println("是错误的邮箱");        }    }}