package day02;//变量的演示public class VarDemo {    public static void main(String[] args) {        //1)变量的声明：相当于开账户        int a; //声明了一个整形的变量，名为a        int b,c,d; //声明了三个整形的变量，名为b，c，d        //int a; //编译错误，变量不能同名        //2)变量的初始化：相当于给账户存钱        int e=250; //声明整形变量e并赋值为250        int f;  //声明整形变量f        f = 250; //声明整形变量250        f = 360; //修改变量f赋值为360        //3)变量的使用：使用的是账户里面的钱        int g=5;        int h=g+10;  //取出g的值5，加10后，再赋值给变量h        System.out.println(h);//输出变量h的值15        System.out.println("h"); //输出h，双引号中的原样输出        g= g+10; //在g本身基础之上增10        System.out.println(g);//输出g的值15        //System.out.println(i);//编译错误，变量i未声明        int i;        //System.out.println(i);//编译错误，变量i未初始化        //4)变量的命名：        int a1,a_5$,_3c,$t;        //int a*b; //编译错误，不能包含*等特殊符号        //int 1a; //编译错误，不能以数字开头        int aa=5;        //System.out.println(aA);//编译错误，严格区分大小写        //int class;//编译错误，不能使用关键字        int 年龄;//允许，但不建议        int nianling; //必须杜绝，既不直观也不专业        int age; // 建议“英文的见名知意 ”        int score,myScore,myJavaScore;//建议“小驼峰命名法”    }}