package day04;import java.util.Scanner;//猜数字小游戏public class Guessing {    public static void main(String[] args) {        Scanner saber = new Scanner(System.in);        int num =(int) (Math.random()*1000+1);//手里藏的数        /*        Math.random()------------0.0到0.99999999999....        *1000--------------------0.0到999.999999999....        +1-----------------------1.0到1000.99999999....        （int）-------------------1到1000         */        System.out.println("菜逼，来！");        int guess = saber.nextInt();        while (guess!=num){            if (guess>num){                System.out.println("太大了");            }else{                System.out.println("太小了");            }            System.out.println("垃圾！再来");            guess = saber.nextInt();        }        System.out.println("哟，居然蒙中了！");    }}