package day04;//成绩等级判断import javax.swing.*;import java.util.Scanner;public class ScoreLevel {    public static void main(String[] args) {        Scanner saber = new Scanner(System.in);        System.out.println("请输入成绩：");        double score = saber.nextDouble();        /*        A:成绩大于等于90        B：成绩大于等于80并且小于90        C:成绩大于等于60并且小于80        D:成绩小于60         */        //带数（888，-56，95，85）        if (score<0 || score>100){            System.out.println("成绩不合法");        }else if (score>=90){ //合法            System.out.println("A-优秀");        }else if (score>=80){            System.out.println("B-良好");        }else if (score>=60){            System.out.println("C-中等");        }else if (score<60){            System.out.println("D-不及格");        }    }}