package day05;//数组的演示public class ArrayDemo {    public static void main(String[] args) {        /*        //1)数组的定义：        int[] arr = new int[]        //2)数组的初始化：初始化的是数组中的元素        int[] arr1 = new int[3];//0,0,0        int[] arr2 = [2,5,8];//2,5,8        int[] arr3 = new int[]{2,5,8};//2,5,8        int[] arr4        //arr4 = {2,5,8};//编译错误        arr4 = new int[]{2,5,8};//正确         */        /*        //3）数组的访问:访问的是数组中的元素       int[] arr = new int[3];       System.out.println(arr[0]);//输出第一个元素        arr[0] = 100;        arr[1] = 200;        arr[2] = 300;        //arr[3] = 400;//运行时会发生ArrayIndexOutOfBoundException数组下标越界异常        System.out.println(arr.length);//输出arr的长度        System.out.println(arr.length-1);//输出最后一个元素的值         */        //4)数组的遍历：从头到尾走一遍        int[] arr = new int[10];        for (int i=0; i<arr.length;i++){//遍历arr数组            //arr[i]代表arr中的每个元素             arr[i]=(int)(Math.random()*100);//给每个元素赋值为0到99的随机数            System.out.println(arr[i]);//输出每个元素的值        }    }}