package day05;import java.util.Scanner;//晚课public class Demo {    public static void main(String[] args) {        /*        int result = 0;        int i = 2;        switch (i){            case 1 :                result = result+1;            case 2 :                result = result+i*2;//result = 4            case 3 :                result = result+i*3;//result = 10        }        System.out.println(result);         */        /*        int a = 0,c = 0;        do {            --c;//c=-1            a--;//a=-1        }while (a>0);        System.out.println(c);         */        //声明浮点型数组scors（成绩数组），包含10个元素        //每个元素为接受用户输入的数据        //计算总分和平均分，并输出        Scanner saber = new Scanner(System.in);        double[] archer = new double[10];        for (int i = 0;i<archer.length;i++){//遍历所有成绩            System.out.println("请输入第"+(i+1)+"个学生的成绩：");            archer[i] = saber.nextDouble();        }        double total = 0.0; //总分        for (int i=0;i<archer.length;i++){ //遍历所有成绩            total+=archer[i]; //累加分数        }        double avg = total/archer.length;//平均分        System.out.println("总分为："+total+",平均分为："+avg);    }}