package day05;//for结构的演示public class ForDemo {    public static void main(String[] args) {        /*         3.for结构：           1）语法：                for（要素1;要素2;要素3;）{                      语句块/循环体---------反复执行的过程 4                }           2）执行过程：           12343243243243243243...2（最后走的是2）         */        for (int times = 0; times < 5; times++) {            System.out.println("吾王剑锋所指，吾等心之所向");        }        System.out.println("吾王美如画中仙");        /*          执行过程：              times = 0 true 输出              times = 1 true 输出              times = 2 true 输出              times = 3 true 输出              times = 4 true 输出              times = 5 false for循环结束              输出继续执行         */        //for中的循环变量num的作用域仅在当前for中        for (int num = 1; num <= 9; num++) {            if (num == 4) {//在某种特定条件下，提前结束循环                break;//跳出循环            }            System.out.println(num + "*9=" + num * 9);        }        /*        执行过程：            num=1 1*9            num=2 2*9            num=3 3*9            num=4 结束循环         */        for (int num = 1; num <= 9; num += 2) {//奇数            System.out.println(num + "*9=" + num * 9);        }        for (int num = 2; num <= 9; num += 2) {//偶数            System.out.println(num + "*9=" + num * 9);        }        //输出9的乘法表，跳过能被3整除的        for (int num = 1; num <= 9; num++) {            if (num % 3 == 0) {//在某种特定条件下，提前结束循环                continue;//跳出循环中剩余语句而进入下一次循环            }            System.out.println(num + "*9=" + num * 9);        }        /*        num=1 1*9=9        num=2 2*9=18        num=3 跳过        num=4 4*9=39        num=5 5*9=45        num=6 跳过        num=7 7*9=63        num=8 8*9=72        num=9 跳过         */        //输出9的乘法表，只要不能被3整除的        for (int num = 1; num <= 9; num++) {            if (num%3!=0){                System.out.println(num + "*9=" + num * 9);            }        }    }}