package day05;//九九乘法表 嵌套循环public class MultiTable {    public static void main(String[] args) {        for (int num=1;num<=9;num++){ //控制行            for (int i=1;i<=num;i++){ //控制列                System.out.print(i+"*"+num+"="+i*num+"\t");//\t两个空位            }            System.out.println();//换行        }        /*          执行过程：外层循环走一次，内层循环走所有次            num=1              i=1 1*1=1              i=2 false                   换行         */    }}