package day05;//小作业public class xiaozuoye {    public static void main(String[] args) {        int sum = 0;        for(int i=2;i<=100;i+=2){            sum+=i;        }        System.out.println("sum="+sum);        int num = 1; //阶乘        for(int i=1;i<=8;i++){            num*=i;        }        System.out.println("num="+num);        for(int i=1;i<=6;i++){            for(int j=1;j<=6-i;j++){ //空格                System.out.print(" ");            }            for(int k=1;k<=2*i-1;k++){                System.out.print("*");            }            System.out.println();        }    }}