package day06;import java.util.Arrays;//数组的演示public class ArrayDemo {    public static void main(String[] args) {        //1）数组的赋值：        int[] saber = {10,20,30,40,50,60};        int[] archer = new int[6];//0,0,0,0,0,0        //saber:源数组        //1:源数组的起始下标        //archer:目标数组        //0:目标数组的起始下标        //4:要复制的元素个数        System.arraycopy(saber,0,archer,0,6);//灵活性好        for (int i=0;i<archer.length;i++){            System.out.println(archer[i]);        }        int[] caster = {10,20,30,40,50};        //caster：原数组        //faker：目标数组        //6：目标数组的长度（元素长度）        //----若目标数组长度>源数组长度，则末尾补默认值        //----若目标数组长度<源数组长度，则将末尾的截掉        int[] faker = Arrays.copyOf(caster,6);//灵活性差        for (int i=0 ; i<faker.length;i++){            System.out.println(faker[i]);        }        int[] Enkidu = {10,20,30,40,50};        //数组的扩容（创建了一个更大的新数组，并将源数组中的数据复制进去了）        Enkidu = Arrays.copyOf(Enkidu,Enkidu.length+1);        for (int i=0 ; i<faker.length;i++){            System.out.println(faker[i]);        }    }}