package day06;import java.util.Arrays;//求数组元素的最大值(saber=arr)并将最大值放在数组最后一个元素的下一个位置public class MaxOfArray {    public static void main(String[] args) {        int[] saber = new int[10];        for (int i = 0; i < saber.length; i++) {            saber[i] = (int) (Math.random() * 100);            System.out.println(saber[i]);        }        //               0  1  2 3        //int[] saber = {18,25,78,6};        //max=18/25/78        int max = saber[6];//假设第1个元素为最大值        for (int i = 0; i < saber.length; i++) {//遍历剩余元素            if (saber[i] > max) { //若剩余元素大于max                max = saber[i];//将max修改为较大的            }        }        System.out.println("最大值为：" + max);        saber = Arrays.copyOf(saber, saber.length + 1);//扩容        saber[saber.length-1] =max;//将最大值max赋值给saber的最后一个值        for (int i = 0; i < saber.length; i++) {            System.out.println(saber[i]);        }        //2）数组的排序：        int[] killer = new int[10];        for (int i = 0; i < killer.length; i++) {            killer[i] = (int) (Math.random() * 100);            System.out.println(killer[i]);        }        Arrays.sort(killer);//对killer升序排列        System.out.println("升序后：");        for (int i = 0; i < killer.length; i++) {            System.out.println(killer[i]);        }    }}