package day06;//方法的演示public class MethodDemo {    public static void main(String[] args) {        //无参无返回值        sher();        //有参无返回值        //fuck();//编译错误，有参则必须传参        //fuck();//编译错误，参数类型必须匹配        fuck("食堂泼辣椒");//String name="食堂泼辣椒"        fuck("砸瓦鲁多");//String name="砸瓦鲁多"        //有参无返回值        jojo("承太郎", 7);//String name="承太郎"，int age=7        jojo("dio", 7);//-----实参        //无参有返回值        double a = getNum();//getNum()的值结束return后的那个数        System.out.println(a);//8.88---模拟对返回值的后续操作        //有参有返回值        int b = god(5,6);        System.out.println(b);//11---模拟返回值的后续操作        int m = 5,n=6;        int c = god(m,n);//传的是m和n里面的数        System.out.println(c);//11--模拟对返回值的后续操作        //无参有返回值        int[] d= testArray();        System.out.println(d.length);//10-----模拟对返回值的后续操作        for (int i=0;i<d.length;i++){//输出每个元素的值--模拟对返回值的后续操作            System.out.println(d[i]);        }    }    //有参无返回值    public static void jojo(String name, int age) {//--------形参        System.out.println("哦哟，居然还敢靠近我" + name + "不走近点的话，怎么奏你" + age + "木大");    }    //有参无返回值    public static void fuck(String wcnmd) {//name=wcnmd 字符串-----形参        System.out.println("老东西，你的东西最没用了" + wcnmd + "cnd");    }    //无参无返回值    public static void sher() {        System.out.println("扣弄dio哒，砸瓦鲁多！");    }    //无参有返回值    public static double getNum() {        // return;//编译错误，return后必须跟一个数        //return"abc";//编译错误，返回值类型必须匹配        return 8.88;//1)结束方法的执行  2）返回结果给调用方    }    //有参有返回值    public static int god(int num1,int num2){        int num = num1+num2;        return num;//返回的是num里面的那个数       // return num1+num2;//返回的是num1与num2的和    }    //无参有返回值    public static int[] testArray() {        int[] arr = new int[10];        for (int i = 0; i < arr.length; i++) {            arr[i] = (int) (Math.random() * 100);        }        return arr;    }}