package ooday02;//学生类public class Student {    //成员变量    String name;    int age;    String address; //的    //构造方法    Student(String name,int age,String address){        this.name = name;        this.age = age;        this.address = address;    }    //方法    void  study(){        System.out.println(name+"在学习...");    }    void sayHi(){        System.out.println("大家好，我叫"+this.name+"，今年"+this.age+"岁了，家住"+this.address);    }}