package ooday03;//引用类型数组的演示public class RefArrayDemo {    public static void main(String[] args) {        Student[] stus = new Student[3];//创建Student数组对象        stus[0] = new Student("zs",22,"jc");//创建Student        stus[1] = new Student("ls",12,"jp");        stus[2] = new Student("pl",32,"jo");        System.out.println(stus[0].name);//输出第1个学生的名字        stus[1].age = 16;//修改第2个学生的年龄为26        stus[2].sayHi();//第3个学生跟大家问好        for (int i=0;i<stus.length;i++){//遍历所有学生            System.out.println(stus[i].name);//输出每个学生的名字            stus[i].sayHi();//每个学生跟大家问好        }    }}