package IO;import java.io.FileInputStream;import java.io.FileNotFoundException;import java.io.FileOutputStream;import java.io.IOException;import java.nio.charset.StandardCharsets;/** * 从文件中读取文本数据 */public class ReadStringDemo {    public static void main(String[] args) throws IOException {        FileInputStream fis  = new FileInputStream("fos.dat");        byte[] data = new byte[1024*10];        int len = fis.read(data);        System.out.println("实际读取的字节数："+len);        String line = new String(data, StandardCharsets.UTF_8).trim();        System.out.println(line.length());        System.out.println(line);        fis.close();    }}