package file;import java.io.File;import java.io.FileFilter;/** * 获取./src/file目录下所有名字过”D“开头的子项 */public class Test {    public static void main(String[] args) {        File dir = new File("./src/file");        if (dir.isDirectory()){            FileFilter filter = new FileFilter() {                @Override                public boolean accept(File file) {                    System.out.println("正在过滤："+file.getName());                    return file.getName().startsWith("D");                }            };            /*            File[]listFiles(FileFilter filter)            该方法会将该目录中每一个子项都作为参数先传给filter的accept方法，            只有accept方法返回为true的子项最终才会被包含在返回的file[]数组            中进行返回。             */            File[] subs = dir.listFiles(filter);            System.out.println("共："+subs.length+"个子项");            for (int i=0;i< subs.length;i++){                File sub  = subs[i];                System.out.println(sub.getName());            }        }    }}