package day06;import com.sun.scenario.effect.impl.sw.sse.SSEBlend_SRC_OUTPeer;import java.util.Scanner;public class yeah {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        System.out.print("王兆毅是脑瘫吗：");        String command = scanner.nextLine();        if (command.equals("是")){            System.out.println("恭喜你答对了，他确实是个脑瘫");        }else if (command.equals("不是")){            System.out.println("你跟他一样是个脑瘫");        }else if (command.equals("我觉得是傻逼")){            System.out.println("而且没鸡鸡" );        }else {            System.out.println("你问答的是啥呀，他就是个脑瘫");        }        System.out.println("漂亮");    }}